import React, { useEffect, useState } from 'react'
import {db, auth} from "../firebase"
import styled from "styled-components"

const RatingsScreenContainer = styled.div`
    position: relative;
    height: calc(100vh - 70px);
    display: block;
    text-align: center;
    overflow: hidden;

`

const H3Tag = styled.h3`
    margin-top: 2em;
    font-size: 26px;
`

const H4Tag = styled.h4`
    margin-top: 1em;
    font-size: 20px;
`

const RatingsContainer = styled.div`
    position: relative;
    display: flex;
    justify-content: space-around;
    align-items: center;
    overflow: hidden;

`

const MovieRatings = styled.div`
    height: 80%;
    width: 40%;
    overflow-y: auto;
    border: 1px solid #fff;
    display: block;
`

const TvRatings = styled.div`
    height: 80%;
    width: 40%;
    overflow-y: auto;
    border: 1px solid #fff;
    display: block;



`


const MyRatings = () => {

    const [movieRatings, setMovieRatings] = useState([]);
    const [tvRatings, setTvRatings] = useState([]);

    useEffect(() => {
        const movieIds = []
        const movies = [];
        const showIds = [];
        const shows = [];

        // get movies
        const subscriber = db.collection("movies").doc(auth.currentUser.uid).collection("reviews").orderBy("timestamp", "desc").get()
        .then((querySnap) => {
            if (querySnap) {
                querySnap.forEach((docSnap) => {
                    movieIds.push(docSnap.id);
                });
            }})
        .catch(err => {console.log(err);})



    }, [])
  return (
    <RatingsScreenContainer>
        <H3Tag>My Ratings</H3Tag>
        <RatingsContainer>
            <MovieRatings>
                <H4Tag>Movie Ratings</H4Tag>


            </MovieRatings>
            <TvRatings>
                <H4Tag>TV Show Ratings</H4Tag>


            </TvRatings>
        </RatingsContainer>

    </RatingsScreenContainer>
  )
}

export default MyRatings
