import React, { useState, useEffect } from 'react'
import { useSearchParams } from 'react-router-dom';
import axios from "axios";
import styled from "styled-components";

const SearchResultsContainer = styled.div`
    position: relative;
    overflow: hidden;
    height: calc(100vh - 70px);
    overflow: auto;
    justify-content: center;
    display: flex;
`

const TableSearchResults = styled.table`
    padding: 10px;
    width: 80vw;
`

const TBodySearchResults = styled.tbody`
    display: block;


`

const TRowSearchResults = styled.tr`
    display: flex;
    height: 160px;
`

const TDSearchResults = styled.td`
    position: relative;
    background-color: #353935;
    display: block;
    overflow-y: auto;
    width: calc(80vw - 125px);
`


const SearchResults = () => {
    const [searchParams, setSearchParams] = useSearchParams();
    const [results, setResults] = useState([]);
    const API_KEY = "9a637b178d3f0923dd00ef0550b24e90";
    const MOVIE_SEARCH_URL = "https://api.themoviedb.org/3/search/movie?"

    useEffect(() => {
        const title = searchParams.get("title");
        console.log(title);
        if (title) {
            console.log("title exists: " + title);
            axios.get(MOVIE_SEARCH_URL + new URLSearchParams({api_key: API_KEY, query : title}))
            .then((resp) => {
                console.log(resp.data.results);
                setResults(resp.data.results);


            })
            .catch(err => {console.log(err)})
        }


    }, [searchParams])

    const DisplaySearchResults = () => {
        console.log("displaying search results...");
        return (
            <TableSearchResults>
                <TBodySearchResults>
                    {results.map(movie => (
                        <TRowSearchResults key={movie.id}>
                            <TDSearchResults style={{backgroundColor: 'transparent'}}>
                                <img width="125" height="175" src={'https://image.tmdb.org/t/p/original/' + movie.poster_path} alt="No Movie Poster"/>
                            </TDSearchResults>
                            <TDSearchResults>
                                <h3>{movie.original_title}</h3>
                                <h5>Release Date: {movie.release_date}</h5>
                                <h5>Avg. Rating: {movie.vote_average}</h5>
                                <p>{movie.overview}</p>
                            </TDSearchResults>

                        </TRowSearchResults>
                    ))}
                </TBodySearchResults>
            </TableSearchResults>


        )
    }

  return (
    <SearchResultsContainer>
        {results ? <DisplaySearchResults/> :
        <p>No search results</p>
        }
    </SearchResultsContainer>
  )
}

export default SearchResults;
