import React, {useState} from 'react'
import {Button} from "@mui/material"
import {db, auth} from "../firebase.js"
import firebase from "firebase/compat/app"
import styled from "styled-components"
import Attachment from './svg/Attachment.js'

const SendMessageForm = styled.form`
    position: absolute;
    bottom: 0;
    left: 20%;
    width: 100%;
    height: 30px;
    display: flex;
    align-items: center;
`

const SendMessageInput = styled.input`
    width: 40vw;
    margin: 0px 10px 10px;
    padding: 10px;
    border-radius: 5px;
    outline: none;
    border: none;
`


function SendMessage(handleSubmit, text, setText) {

  return (
    <div>
        <SendMessageForm onSubmit={sendMessage}>
            <label htmlFor='img'>
                <Attachment/>
            </label>
            <input type='file' id='img' accept='image/*' style={{display: 'none'}}>
            </input>
            <SendMessageInput value={text} onChange={(e)=> setText(e.target.value)} placeholder="Message..."/>
            <Button type="submit">Send</Button>
        </SendMessageForm>

    </div>
  )
}

export default SendMessage
