import React, {useEffect, useState} from 'react'
import {db, auth, storage} from '../firebase'
import styled from "styled-components"
import User from '../components/User';
import Moment from "react-moment"
import {query, onSnapshot, orderBy, collection, getDoc, updateDoc, doc} from "firebase/firestore"
import { style } from '@mui/system';
import { useNavigate } from 'react-router-dom';


const HomeContainer = styled.div`
    position: relative;
    display: grid;
    grid-template-columns: 1fr 3fr 1fr;
    overflow: hidden;
    height: calc(100vh - 70px);
    width: 100vw;
`

const UserContainer = styled.div`
    border-right: 2px solid #000000;
    overflow-y: auto;
`

const FeedContainter = styled.div`
    position: relative;
    // background-color: #F0F8FF;
    background-color: #E6E6FA;
    overflow-y: auto;
    width: 100%;
`

const PostDiv = styled.div`
    margin: 5%;
    background-color: white;
    padding: 10px;
    border-radius: 7px;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.4);
    display: flex;
    gap: 7px;

`
const PicDiv = styled.div`
`

const ProfilePic = styled.img`
    border-radius: 50%;
    width: 40px;
    height: 40px;
`
const PostContentDiv = styled.div`
    display: block;
`

const NameandTimePosted = styled.div`
    margin-bottom: 2px;
`

const MediaImage = styled.img`
    width: 75px;
    height: 100px;
`

const StyledH3 = styled.h3`
    margin: 0;
    padding: 10px;
    text-align: center;
    font-family: 'Dosis', sans-serif;
    color: black;
`

const PostedByName = styled.h4`
    color: black;
    margin-top: 8px;
    margin-bottom: 3px;
`
const ReviewAndPosterDiv = styled.div`
    display: flex;
`

const ReviewRatingDiv = styled.div`
    display: block;
    margin-left: 5px;
`

const RatingText = styled.h4`
    color: black;
    font-family: 'Cabin', sans-serif;
    font-weight: normal;
    margin-top: 4px;
    display: flex;
    margin-bottom: 2px;

`

const momentStyles = {
    color: "grey",
    fontStyle: "italic",
    fontSize: '14px'
}

export const Home = () => {

    const [users, setUsers] = useState([]);
    const [chat, setChat] = useState("");   // this will hold the user that is being chatted with
    const [messages, setMessages] = useState([]);
    const [posts, setPosts] = useState([]);
    const user1 = auth.currentUser?.uid;

    const navigate = useNavigate();

    useEffect(()=>{
        if (auth.currentUser) {
            const usersRef = db.collection("users");
            // create query obj
            usersRef.where('uid', 'not-in', [auth.currentUser?.uid]).get().then((querySnapshot) => {
                let users = []
                querySnapshot.forEach(doc => {
                    users.push(doc.data())
                });
                setUsers(users);
            })
        }

        // get posts
        const getPosts = async () => {
            const postsFirebase = [];
            //check later what would happen if there were no posts
            db.collectionGroup("post").orderBy("createdAt", "desc").get()
            .then(querySnapshot => {
                //console.log(querySnapshot.docs);
                if (querySnapshot.docs.length) {
                    querySnapshot.docs.map(docSnap => {
                        console.log(docSnap.data());
                        postsFirebase.push(docSnap.data());
                    })
                    setPosts(postsFirebase);
                }
            })
        }
        getPosts();

    }, [])

    const selectUser = async (user) => {
        setChat(user);

        const user2 = user.uid;
        const id = user1 > user2 ? `${user1 + user2}` : `${user2 + user1}`;

        const messagesRef = collection(db, "messages", id, "chat");
        const q = query(messagesRef, orderBy("createdAt", "asc"));
        onSnapshot(q, querySnapshot => {
            let msgs = [];
            querySnapshot.forEach(doc => {
                msgs.push(doc.data());
            })
            setMessages(msgs);
        })

        // get last message between the loggen in user and the selected user
        const docSnap = await getDoc(doc(db, "lastMessage", id));
        // checking to see if the last message exists
        if (docSnap.data() && docSnap.data().from !== user1) {
            updateDoc(doc(db, "lastMessage", id), {
                unread : false
            })
        }
    }       

  return (
    <HomeContainer>
        <UserContainer>
            {users.map(user => <User key={user.uid} user={user} selectUser={selectUser} chat={chat}/>
            )}
        </UserContainer>
        <FeedContainter key="feed">
            <StyledH3>Feed</StyledH3>
            {posts.map((post, i) => (
                <PostDiv key={i}>
                    <PicDiv>
                        <ProfilePic src={post.photoURL} alt='No Profile Pic'/>
                    </PicDiv>
                    <PostContentDiv>
                        <NameandTimePosted>
                            <PostedByName>{post.name}</PostedByName>
                            <Moment style={momentStyles}fromNow>{post.createdAt.toDate()}</Moment>
                        </NameandTimePosted>
                        <ReviewAndPosterDiv>
                            {/*<MediaImage src={post.mediaPoster} alt="No Poster"/>*/}
                            <ReviewRatingDiv>
                                <RatingText>just gave 
                                    <span style={{color: 'red', lineHeight: '18px', fontSize: '18px', fontWeight: '600', whiteSpace: 'pre'}}> {post.title}&nbsp;</span>
                                    a rating of
                                    <span style={{color: '#008080', lineHeight: '18px', fontSize: '18px', whiteSpace: 'pre'}}> {post.rating}‚≠ê&nbsp;</span>
                                    !!!
                                </RatingText>
                            </ReviewRatingDiv>
                        </ReviewAndPosterDiv>
                    </PostContentDiv>
                </PostDiv>
            ))}

        </FeedContainter>
    </HomeContainer>

  )
}

export default Home;
