import React, {useEffect, useState} from 'react'
import styled from "styled-components"
import {onSnapshot, collection, doc} from "firebase/firestore"
import {db} from "../firebase"


const UserWrapper = styled.div`
    margin-bottom: 10px;
    padding: 10px;
    cursor: pointer;
`

const UserInfo = styled.div`
    display: flex;
    justify-content: space-between;
    align-items: center;
`

const LastMessageP = styled.p`
  font-size: 14px;
  white-space: nowrap;
  width: 90%;
  overflow: hidden;
  text-overflow: ellipsis;
`



const User = ({user, selectUser, user1}) => {
    const user2 = user?.uid;
    const [data, setData] = useState('');

    useEffect(() => {
        const id = user1 > user2 ? `${user1 + user2}` : `${user2 + user1}`;
        console.log(id);
        let unsub = onSnapshot(doc(db, "lastMessage", id), (doc) => {
            console.log("in snapshot");
            setData(doc.data());
        })

        return () => unsub();
    }, [])
    console.log(data);


  return (
    <UserWrapper onClick={() => selectUser(user)}>
        <UserInfo>
            <img width='50' height='50' style={{borderRadius: '50%'}} src={user.photoURL} alt="Profile pic"/>
            <p>{user.name}</p>
        </UserInfo>
        {data && (
            <LastMessageP className='truncate'>{data.text}</LastMessageP>
        )}
    </UserWrapper>
  )
}

export default User
