import firebase from 'firebase/compat/app';
import 'firebase/compat/auth';
import 'firebase/compat/firestore';
import 'firebase/compat/storage';


const firebaseApp = firebase.initializeApp({
    apiKey: "AIzaSyC3wKzqaH5jeCnyhGzzQV9-OhqjcRu4xHg",
    authDomain: "movieratingsideproject.firebaseapp.com",
    projectId: "movieratingsideproject",
    storageBucket: "movieratingsideproject.appspot.com",
    messagingSenderId: "835870990818",
    appId: "1:835870990818:web:5bf629c5f97b6b49e259bb"
});

const db = firebaseApp.firestore();
const storage = firebaseApp.storage();

const auth = firebase.auth();


export {db, auth, storage};
