import React, { useEffect, useState } from 'react'
import {db, auth} from "../firebase"
import styled from "styled-components"
import axios from 'axios'

const RatingsScreenContainer = styled.div`
    position: relative;
    height: calc(100vh - 70px);
    display: block;
    text-align: center;
    overflow: hidden;
`

const H3Tag = styled.h3`
    margin-top: 2em;
    font-size: 26px;
`

const H4Tag = styled.h4`
    margin-top: 1em;
    font-size: 20px;
`

const RatingsContainer = styled.div`
    position: relative;
    display: flex;
    justify-content: space-around;
    align-items: center;
    overflow: hidden;

`

const MovieRatings = styled.div`
    height: 80%;
    width: 40%;
    overflow-y: auto;
    border: 1px solid #fff;
    display: block;
`

const TvRatings = styled.div`
    height: 80%;
    width: 40%;
    overflow-y: auto;
    border: 1px solid #fff;
    display: block;
`

const Table = styled.table`
    padding: 10px;
`

const TBody = styled.tbody`
    display: block;
`

const TRow = styled.tr`
    display: flex;
    margin: 15px;
    align-items: center;
`


const MyRatings = () => {

    const [movieRatings, setMovieRatings] = useState([]);
    const [tvRatings, setTvRatings] = useState([]);
    const API_KEY = "9a637b178d3f0923dd00ef0550b24e90";
    const TV_DETAIL_URL = "https://api.themoviedb.org/3/tv/"; 
    const MOVIE_DETAIL_URL = "https://api.themoviedb.org/3/movie/"; 
    // + movieId + 
    //     "?" + new URLSearchParams({api_key: API_KEY});

    useEffect(() => {
        const movieIds = []
        const movies = [];
        const showIds = [];
        const shows = [];

        // get movies
        const subscriber = db.collection("movies").doc(auth.currentUser.uid).collection("reviews").orderBy("timestamp", "desc").get()
        .then((querySnap) => {
            if (querySnap) {
                querySnap.forEach((docSnap) => {
                    movieIds.push(docSnap.id);
                });
            }
            console.log(movieIds);

            // iterate through the movieIds array and retrieve movie data and append to movies array
            if (movieIds.length) {
                movieIds.map(movieId => {
                    axios.get(MOVIE_DETAIL_URL + movieId + "?" + new URLSearchParams({api_key: API_KEY}))
                    .then(resp => {
                        // console.log(resp);
                        movies.push(resp.data);
                        setMovieRatings(movies);
                    })
                })
            }
        }) 
        .catch(err => {console.log(err);})

        // get tv shows

        const sub = db.collection("tv").doc(auth.currentUser.uid).collection("reviews").orderBy("timestamp", "desc").get()
        .then((querySnap) => {
            if (querySnap) {
                querySnap.forEach((docSnap) => {
                    showIds.push(docSnap.id);
                });
            }
            console.log(showIds);

            // iterate through the movieIds array and retrieve movie data and append to movies array
            if (showIds.length) {
                showIds.map(showId => {
                    axios.get(TV_DETAIL_URL + showId + "?" + new URLSearchParams({api_key: API_KEY}))
                    .then(resp => {
                        // console.log(resp);
                        shows.push(resp.data);
                        setTvRatings(shows);
                    })
                })
            }
        }) 
        .catch(err => {console.log(err);})
    }, [])


  return (
    <RatingsScreenContainer>
        <H3Tag>My Ratings</H3Tag>
        <RatingsContainer>
            <MovieRatings>
                <H4Tag>Movie Ratings</H4Tag>
                <Table>
                    <TBody>
                        {}


                    </TBody>
                </Table>



            </MovieRatings>
            <TvRatings>
                <H4Tag>TV Show Ratings</H4Tag>


            </TvRatings>
        </RatingsContainer>

    </RatingsScreenContainer>
  )
}

export default MyRatings
