import React, { useState, useEffect } from 'react'
import { useSearchParams } from 'react-router-dom';
import axios from "axios";
import styled from "styled-components";

const SearchResultsContainer = styled.div`
    position: relative;
    overflow: hidden;
    height: calc(100vh - 70px);
    overflow: auto;
    justify-content: center;
    display: flex;
`

const TableSearchResults = styled.table`
    padding: 10px;
    width: 80vw;
`

const TBodySearchResults = styled.tbody`
    display: block;


`

const TRowSearchResults = styled.tr`
    display: flex;
    height: 175px;
    margin: 15px;
    align-items: center;
`

const TDMoviePicture = styled.td`
    height: 175px;
    width: 125px;
    box-shadow: 0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.99);

`


const TDMovieInfo = styled.td`
    background: rgb(64,78,240);
background: linear-gradient(342deg, rgba(64,78,240,1) 22%, rgba(189,85,247,1) 100%);
    color: white;
    display: block;
    overflow-y: auto;
    margin-left: 10px;
    height: 140px;
    padding: 5px 10px;

`


const SearchResults = () => {
    const [searchParams, setSearchParams] = useSearchParams();
    const [results, setResults] = useState([]);
    const API_KEY = "9a637b178d3f0923dd00ef0550b24e90";
    const MOVIE_SEARCH_URL = "https://api.themoviedb.org/3/search/movie?"

    useEffect(() => {
        const title = searchParams.get("title");
        console.log(title);
        if (title) {
            console.log("title exists: " + title);
            axios.get(MOVIE_SEARCH_URL + new URLSearchParams({api_key: API_KEY, query : title}))
            .then((resp) => {
                console.log(resp.data.results);
                setResults(resp.data.results);


            })
            .catch(err => {console.log(err)})
        }


    }, [searchParams])

    const DisplaySearchResults = () => {
        console.log("displaying search results...");
        return (
            <TableSearchResults>
                <TBodySearchResults>
                    {results.map(movie => (
                        <TRowSearchResults key={movie.id}>
                            <TDMoviePicture style={{}}>
                                <img style={{borderRadius: '3px'}} width="125" height="175" src={'https://image.tmdb.org/t/p/original/' + movie.poster_path} alt="No Movie Poster"/>
                            </TDMoviePicture>
                            <TDMovieInfo style={{width: 'calc(80vw - 125px)', borderRadius: '10px'}}>
                                <h3 style={{color: 'black'}}>{movie.original_title}</h3>
                                <h5>Release Date: {movie.release_date}</h5>
                                <h5>Avg. Rating: {movie.vote_average}</h5>
                                <p>{movie.overview}</p>
                            </TDMovieInfo>

                        </TRowSearchResults>
                    ))}
                </TBodySearchResults>
            </TableSearchResults>


        )
    }

  return (
    <SearchResultsContainer>
        {results ? <DisplaySearchResults/> :
        <p>No search results</p>
        }
    </SearchResultsContainer>
  )
}

export default SearchResults;
