import React, { useState, useEffect } from 'react'
import { useSearchParams } from 'react-router-dom';
import axios from "axios";
import styled from "styled-components";

const SearchResultsContainer = styled.div`
    position: relative;
    width: 100vw;
    height: calc(100vh - 70px);
    align-items: center;
    justify-content: center;
    display: flex;
`

const TableSearchResults = styled.table`
    width: 70vw;
    height: 90%;
`

const TBodySearchResults = styled.tbody`
    display: block;
    overflow-y: auto;


`

const TRowSearchResults = styled.tr`

`


const SearchResults = () => {
    const [searchParams, setSearchParams] = useSearchParams();
    const [results, setResults] = useState([]);
    const API_KEY = "9a637b178d3f0923dd00ef0550b24e90";
    const MOVIE_SEARCH_URL = "https://api.themoviedb.org/3/search/movie?"

    useEffect(() => {
        const title = searchParams.get("title");
        console.log(title);
        if (title) {
            console.log("title exists: " + title);
            axios.get(MOVIE_SEARCH_URL + new URLSearchParams({api_key: API_KEY, query : title}))
            .then((resp) => {
                console.log(resp);
                setResults(resp.data.results);


            })
            .catch(err => {console.log(err)})
        }


    }, [searchParams])

    const DisplaySearchResults = () => {
        console.log("displaying search results...");
        return (
            <TableSearchResults>
                <TBodySearchResults>
                    {results.map(movie => (
                        <TRowSearchResults key={movie.id}>
                            <td>
                                <img width="150" height="200" src={'https://image.tmdb.org/t/p/original/' + movie.poster_path} alt="No Movie Poster"/>
                            </td>
                            <td>Hello</td>

                        </TRowSearchResults>
                    ))}
                </TBodySearchResults>
            </TableSearchResults>


        )
    }

  return (
    <SearchResultsContainer>
        {results ? <DisplaySearchResults/> :
        <p>No search results</p>
        }
    </SearchResultsContainer>
  )
}

export default SearchResults;
