import React, {useEffect, useState} from 'react'
import {db, auth} from '../firebase'

export const Home = () => {

    const [users, setUsers] = useState([]);

    useEffect(()=>{
        if (auth.currentUser) {
            const usersRef = db.collection("users");
            // create query obj
            const querySnapshot = usersRef.where('uid', 'not-in', [auth.currentUser?.uid]).get();

            if (querySnapshot.empty) {
                console.log("No other users");
            }
            else {
                let users = []
                querySnapshot.forEach(doc => {
                    users.push(doc.data())
                });
                setUsers(users);
            }
        }

    }, [])
    console.log(users);


  return (
    <div>Home Page</div>
  )
}


export default Home;
