import React, {useEffect, useState} from 'react'
import {db, auth, storage} from '../firebase'
import { getAuth, onAuthStateChanged } from "firebase/auth";
import styled from "styled-components"
import User from '../components/User';
import SendMessage from '../components/SendMessage';
import firebase from "firebase/compat/app"
import {ref, getDownloadURL, uploadBytes} from "firebase/storage"


const HomeContainer = styled.div`
    position: relative;
    display: grid;
    grid-template-columns: 1fr 3fr;
    overflow: hidden;
    height: calc(100vh - 70px);
    width: 100vw;
    background-color: #FCAF45;
`

const UserContainer = styled.div`
    margin-top: 10px;
    border-right: 2px solid #000000;
    overflow-y: auto;
`

const ChatContainter = styled.div`
    position: relative;
    width: 100%;
`

const MessagesUser = styled.div`
    padding:10px;
    text-align: center;
    border-bottom: 2px solid #000000;
`

export const Home = () => {

    const [users, setUsers] = useState([]);
    const [chat, setChat] = useState("");
    const [text, setText] = useState("");
    const [image, setImage] = useState("");
    const [messages, setMessages] = useState([]);

    const authorize = getAuth();
    let user1 = null;
    onAuthStateChanged(authorize, (user) => {
        if (user) {
            user1 = auth.currentUser.uid;
        }}
    );

    useEffect(()=>{
        const authorize = getAuth();
        onAuthStateChanged(authorize, (user) => {
            if (user) {
                const usersRef = db.collection("users");
                // create query obj
                usersRef.where('uid', 'not-in', [auth.currentUser?.uid]).get().then((querySnapshot) => {
                    let users = []
                    querySnapshot.forEach(doc => {
                        users.push(doc.data())
                    });
                    setUsers(users);
                })
            } else {
                // User is signed out
            }
        });
    }, [])
    console.log(users[0]);

    const selectUser = (user) => {
        setChat(user);
        console.log(user);

        const user2 = user.uid;
        const id = user1 > user2 ? `${user1 + user2}` : `${user2 + user1}`;

        const messagesRef = db.collection("messages").doc(id).collection("chat");
        messagesRef.orderBy("createdAt", "asc").get().then(
            (querySnapshot) => {
                let msgs = [];
                querySnapshot.forEach(doc => {
                    msgs.push(doc.data());
                })
                setMessages(msgs);
            }
        )
    }
    console.log(messages);

    const handleSubmit = async e => {
        e.preventDefault();
        const user2 = chat.uid;
        // make document id
        const id = user1 > user2 ? `${user1 + user2}` : `${user2 + user1}`;

        let url;
        if (image) {
            const imageRef = ref(storage, `images/${new Date().getTime()} - ${image.name}`)
            const snap = await uploadBytes(imageRef, image);
            const downloadUrl = await getDownloadURL(ref(storage, snap.ref.fullPath));
            url = downloadUrl;
        }


        await db.collection("messages").doc(id).collection("chat").add({
            text,
            from: user1,
            to: user2,
            createdAt: firebase.firestore.FieldValue.serverTimestamp(),
            media: url || ""
        })
        setText("");
    }

  return (
    <HomeContainer>
        <UserContainer>
            {users.map(user => <User key={user.uid} user={user} selectUser={selectUser}/>
            )}
        </UserContainer>
        <ChatContainter>
            {chat ? (
                <>
                    <MessagesUser>
                    <h3>{chat.name}</h3>
                    </MessagesUser>
                    <SendMessage 
                        handleSubmit={handleSubmit} 
                        text={text} 
                        setText={setText}
                        setImage={setImage}
                    />
                </>

            ) : (
                <h3 style={{textAlign: 'center'}}>Select a user to start a conversation</h3>
            )}
        </ChatContainter>
    </HomeContainer>

  )
}



export default Home;
